import React from 'react';
import './App.css';



class App extends React.Component {

    constructor(props) {
        super(props)
        this.state = {dataset_name: '', dataset_id: ''} //{}

        this.handleChange = this.handleChange.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)
    }

    handleChange = ({target}) => {
        this.setState({[target.name]: target.value})
    }

    handleSubmit = (event) => {
        event.preventDefault()
        let obj = {}
        obj.dataset_name = this.state.dataset_name
        obj.dataset_id = this.state.dataset_id
        fetch(
            "http://localhost:5000/data", {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(obj),
        })
            .then(response => response.json())
            .then(obj => {
                console.log('Success:',obj)
            })
            .catch((error) => {
                console.error('Error:', error)
            })
    }
    render() {
        return (
            <div>
            <form onSubmit={this.handleSubmit}>
                <label>
                    Enter the DataSet Name
                    <input type= "text"
                           id = "first"
                           name = "dataset_name"
                           value= {this.state.dataset_name}
                           onChange= {this.handleChange}
                           className= "custom_box"
                           placeholder= "Value #1"
                    />
                                    </label>

                    <input type= "text"
                           name = "dataset_id"
                           value= {this.state.dataset_id}
                           onChange= {this.handleChange}
                           className= "custom_box"
                           placeholder= "Value #2"
                    />
                    <input type = "file" className= "custom_upload" ref={this.fileInput}/>
                    <input type = "file" className= "custom_upload"/>
                    <input type = "file" className= "custom_upload"/>
                    <input type = "file" className= "custom_upload"/>
                    <input type= "submit"
                       value= "Submit"
                       className= "custom_box"
                    />


            </form>
            </div>
        )
    }
}
