import React from 'react';
import './App.css';



class App extends React.Component {

    constructor(props) {
        super(props)
        this.state = {dataset_name: '', dataset_id: '', selectedFiles: null} //{}

        this.handleChange = this.handleChange.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)
        this.handleFileChange = this.handleFileChange.bind(this)
    }

    handleChange = ({target}) => {
        this.setState({[target.name]: target.value})
    }
    handleFileChange = event => {
        this.setState({
            selectedFiles: event.target.files,
        })

    }

    handleSubmit = (event) => {
        event.preventDefault()

        let obj = {}
        obj.dataset_name = this.state.dataset_name
        obj.dataset_id = this.state.dataset_id

        fetch(
            "http://localhost:5000/data", {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(obj),
            })
            .then(response => response.json())
            .then(obj => {
                console.log('Success:', obj)
            })
            .catch((error) => {
                console.error('Error:', error)
            })
        const fileData = new FormData()
        fileData.filesToSend = this.state.selectedFiles
        fetch(
            "http://localhost:5000/data", {
            method: 'POST',
            headers: {
                'Content-Type': 'text/plain'
            },
            body: fileData,
            })
            .then(response => response.json())
            .then(fileData => {
                console.log('Success:',fileData)
            })
            .catch((error) => {
                console.error('Error:',error)
            })
    }
