import React from 'react';
import './App.css';



class App extends React.Component {

    constructor(props) {
        super(props)
        this.state = {dataset_name: '', dataset_id: '', selectedFiles: null} //{}

        this.handleChange = this.handleChange.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)
        this.handleFileChange = this.handleFileChange.bind(this)
    }

    handleChange = ({target}) => {
        this.setState({[target.name]: target.value})
    }
    handleFileChange = event => {
        this.setState({
            selectedFiles: event.target.files,
        })

    }

    handleSubmit = (event) => {
        let count = 1
        event.preventDefault()
        let fileData = new FormData()
        //fileData.filesToSend = this.state.selectedFiles
        console.log(this.state.selectedFiles[0].name)
        //for (let i = 0; i < this.state.selectedFiles.length; i++){
          //  fileData.append('file' + count.toString(),this.state.selectedFiles[i])
            //fileData.append('filename' + count.toString(), this.state.selectedFiles[i].name)
        //}
        fileData.append('file' + count.toString(),this.state.selectedFiles[0])
        fileData.append('filename' + count.toString(), this.state.selectedFiles[0].name)
        console.log(fileData)
        //let obj = {}
        //obj.dataset_name = this.state.dataset_name
        //obj.dataset_id = this.state.dataset_id
        fetch(
            "http://localhost:5000/data", {
                method: 'POST',
                //headers: {
                  //  'Content-Type': 'application/json'
                //},
                //JSON.stringify(obj)
                body: fileData,

            })
            .then(response => response.json())
            .then(obj => {
                console.log('Success:', fileData)
            })
            .catch((error) => {
                console.error('Error:', error)
            })

    }
