import React from 'react';
import './App.css';



class App extends React.Component {

    constructor(props) {
        super(props)
        this.state = {dataset_name: '', dataset_id: '', selectedFiles: null} //{}

        this.handleChange = this.handleChange.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)
        this.handleFileChange = this.handleFileChange.bind(this)
    }

    handleChange = ({target}) => {
        this.setState({[target.name]: target.value})
    }
    handleFileChange = event => {
        this.setState({
            selectedFiles: event.target.files,
        })

    }

    handleSubmit = (event) => {
        event.preventDefault()
        const fileData = new FormData()
        fileData.filesToSend = this.state.selectedFiles
        let obj = {}
        obj.dataset_name = this.state.dataset_name
        obj.dataset_id = this.state.dataset_id

        fetch(
            "http://localhost:5000/data", {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(obj), fileData,
        })
            .then(response => response.json())
            .then(obj => {
                console.log('Success:',obj, fileData)
            })
            .catch((error) => {
                console.error('Error:', error)
            })

    }
    render() {
        return (
            <div>
            <form onSubmit={this.handleSubmit} className="parent">
                <label className="custom_text_placement">
                    Enter the DataSet Name
                    <input type= "text"
                           id = "first"
                           name = "dataset_name"
                           value= {this.state.dataset_name}
                           onChange= {this.handleChange}
                           className= "custom_box"
                    />
                </label>
                <label className="custom_text_placement">
                    Enter the DataSet ID
                    <input type= "number"
                           name = "dataset_id"
                           value= {this.state.dataset_id}
                           onChange= {this.handleChange}
                           className= "custom_box"
                    />
                </label>
                <label>
                    Please select up to 4 files:
                    <input type = "file" className= "custom_upload" onChange={this.handleFileChange} multiple/>
                </label>
                    <input type= "submit"
                       value= "Submit"
                       className= "custom_box"
                    />



            </form>
            </div>
        )
    }
}
    export default App
